import React from 'react';
import './Menu-item.scss';

const MenuItem = ({title, subtitle , image}) => {
    return ( 
        <div 
            className="menu-item"
            style={{backgroundImage: `url($image)`}}
        >
            <div className="content">
                <h1>{title}</h1>
                <span> Shop Now</span>
            </div>
        </div>
     );
}

export default MenuItem;
