import React,{useState} from 'react';
import {signInWithGoogle,auth,createUserProfileDocument} from '../../firebase/firebase.utils';
import '../../styles/forms.scss'

export const FormInput = ({handleChange, label, ...otherProps}) => {
    return (
        <div className="group">
            <input 
                name={otherProps.name}
                type={otherProps.type}
                value={otherProps.value}
                className='form-input' 
                onChange={(e)=>handleChange(e.target.value)}
                required={otherProps.required}
            />
                    <label for={otherProps.name} className={`${otherProps.value.length ? 'shrink' : '' }`}>
                        {label}
                    </label>
        </div>
    )
}

export const Button = ({title,isGoogleSignIn , ...otherProps}) => {
    return (
        <button 
        {...otherProps}
        className={`${ isGoogleSignIn ? 'google-sign-in' : '' } custom-button title`} 
        >
            {title}
        </button>
    )
}

export const SignIn = () => {
    const [Email, setEmail] = useState('');
    const [Password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        setPassword('');
        setEmail('');
    }
    return ( 
        <div className='sign-in'>
            <h2> I already have an account</h2>
            <span> Sign in with your email and password</span>

            <form onSubmit={(e) => handleSubmit(e)}>
                <FormInput
                     name='email' 
                     type='email' 
                     handleChange={setEmail}
                     label='Email'
                     value={Email} 
                     required 
                />
                <FormInput 
                    name='password' 
                    type='password' 
                    handleChange={setPassword} 
                    label='Password'
                    value={Password} 
                    required
                />

                <div className='buttons'>
                    <Button type='submit' title='Sign In'/>

                    <Button onClick={()=> signInWithGoogle()} isGoogleSignIn title='Sign In With Google'/>

                </div>
            </form>
        </div>
     );
}

export const SignUp = () => {
    const data = {
        displayName: "",
        email: "",
        password: "",
        confirmPassword: ""
    }
    const [signUpForm, setsignUpForm] = useState(data);

    const handleChange = (name, value) => {
        setsignUpForm({...signUpForm, [name]: value});
    }

    const handleSubmit = async (e) => {
         e.preventDefault();
         const {displayName,email,password,confirmPassword} = signUpForm;
         if(password !== confirmPassword){
             alert("passwords don't match");
             return;
         }
         try{
             const {user} = await auth.createUserWithEmailAndPassword(email,password);

             await createUserProfileDocument(user, {displayName});
         } catch (error) {
             console.log(error);
             alert(error);
         }
    }
     return ( 
         <div className="sign-up">
             <h2 className="title"> I do not have an account</h2>
             <span>Sign up with your email and password</span>
             <form className="sign-up-form" onSubmit={(e)=>handleSubmit(e)}>
                 <FormInput
                     type="text"
                     name="displayName"
                     value={signUpForm.displayName}
                     onChange={(e) => handleChange(e.target.name,e.target.value)}
                     label="Display Name"
                     required
                 />
                 <FormInput
                     type="email"
                     name="email"
                     value={signUpForm.email}
                     onChange={(e) => handleChange(e.target.name,e.target.value)}
                     label="Email"
                     required
                 />
                 <FormInput
                     type="text"
                     name="password"
                     value={signUpForm.password}
                     onChange={(e) => handleChange(e.target.name,e.target.value)}
                     label="Password"
                     required
                 />
                 <FormInput
                     type="text"
                     name="confirmPassword"
                     value={signUpForm.confirmPassword}
                     onChange={(e) => handleChange(e.target.name,e.target.value)}
                     label="Confirm Password"
                     required
                 />
                 <Button type="" title="Sign Up"/>
             </form>
         </div>
      );
 }
