import React from 'react';
import {Redirect} from "react-router-dom"
import history from "../../helpers/history";
import '../../styles/collection-preview.styles.scss'
import CollectionItem from '../collection-item/CollectionItem';

const PreviewCollection = ({title, items,name,match}) => {
    const SwitchRoute = () => {
        // return <Redirect to={`/shop/${name}`} />
        return history.createBrows(`${match.path}/${name}`)
    }
    return ( 
        <div className='collection-preview'>
            <h1 className='title' onClick={()=> SwitchRoute()}>{title.toUpperCase()}</h1>
            <div className='preview'>
                {items.filter((item,index) => index < 4 ).map((item) => (
                    <CollectionItem key={item.id} item={item} />
                ))}
            </div>
        </div>
     );
}

export default PreviewCollection;
