import React from 'react';
import './Menu-item.scss';

const MenuItem = ({title, image, size}) => {
    return ( 
        <div 
            className={`${size}  content`}
            style={{
                backgroundImage: `url(${image})`
                }}
        >
            <div className={`${size}  content`}>
                <h1>{title}</h1>
                <span> Shop Now</span>
            </div>
        </div>
     );
}

export default MenuItem;
