import { firestore } from "../firebase/firebase.utils";

const initialState={
    user: null,
}

const Shop = (state=initialState, action) => {
    switch (action.type) {
        case "LOAD_USER":

            return {
              ...state,
              user: action.user,
            };

        default:
            return state;
    }
}

export default Shop;
